/** @format */
import React, { Fragment, useEffect } from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import NavbarF from "./components/layout/NavbarF";
import Footer from "./components/layout/Footer";
import AdminRegister from "./DashboardComponents/auth/register";
import AdminLogin from "./DashboardComponents/auth/login";
import setAuthToken from "./utills/setAuthToken";
import "../src/components/rtl.css";
import { Provider, provider } from "react-redux";
import store from "./store";
import { laodUser } from "./actions/auth";
import Articles from "./Pages/Article";
import Researches from "./Pages/Researches";
import "./components/layout/main.css";

if (localStorage.token) {
  setAuthToken(localStorage.token);
}

const App = () => {
  useEffect(() => {
    store.dispatch(laodUser);
  }, []);
  return (
    <Provider store={store}>
      <Router>
        <Fragment>
          <NavbarF />
          <section className="container">
            <Switch>
              <Route exact path="/register" component={AdminRegister} />
              <Route exact path="/login" component={AdminLogin} />
              <Route exact path="/Articles" component={Articles} />
              <Route exact path="/Researches" component={Researches} />
            </Switch>
          </section>
          <Footer />
        </Fragment>
      </Router>
    </Provider>
  );
};
export default App;
