/** @format */

import React, { useEffect } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Container from "react-bootstrap/Container";
import FormControl from "react-bootstrap/FormControl";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Home from "../../Pages/Home";
import Researches from "../../Pages/Researches";
import Research from "../../Pages/Research";
import Articles from "../../Pages/Article";
import Article from "../../Pages/Articles";
import Videos from "../../Pages/Videos";
import Books from "../../Pages/Books";
import Links from "../../Pages/Links";
import Questions from "../../Pages/Questions";
import Dashboard from "../../DashboardComponents/Dashboard";
import Login from "../../DashboardComponents/Login";
import Register from "../../DashboardComponents/Register";
import setAuthToken from "../../utills/setAuthToken";
import "../../components/rtl.css";
import { laodUser } from "../../actions/auth";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { logout } from "../../actions/auth";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,

      title: "Islamic psychology",
      Home: {
        title: "Something goes here about home page",
        subTitle: "Our Team ",
      },

      Researches: {
        title: "Check out our latest researches ",
        subTitle: "Researches ",
      },

      Research: {
        title: "Check out our latest researches ",
        subTitle: "Research Name ",
      },

      Articles: {
        title: "Check out Articles...",
        subTitle: "articles ",
      },

      Article: {
        title: "Check out Articles...",
        subTitle: "article Name ",
      },

      Videos: {
        title: "Watch interseting Videos about...",
        subTitle: "videos ",
      },

      Books: {
        title: "Check out the availble books",
        text: "Click on the photos below to see more details!",
      },

      Links: {
        title: "Check out links...",
        subTitle: "Links!",
      },
      Questions: {
        title: "FAQ...",
        subTitle: "Find out answers for your Questions !",
      },

      Dashboard: {
        title: "Welcom to the Control Panel ",
        subTitle: "Please insert your Username and Password  !",
      },

      Register: {
        title: "Welcom to the  control Panel ",
        subTitle:
          "Please insert a Username and Password to register a new admin  !",
      },

      Login: {
        title: "Log in",
        subTitle: "Please insert your Username and Password to log in  !",
      },
    };
  }

  render() {
    return (
      <Router>
        <Navbar bg="light" expand="lg">
          <Navbar.Brand className="NavIteamsStyle  ">
            علم النفس الإسلامي{" "}
          </Navbar.Brand>

          <Navbar.Toggle aria-controls="basic-navbar-nav" />

          <Navbar.Collapse
            toggle={this.state.collapse ? 1 : 0}
            id="basic-navbar-nav"
          >
            <Nav className=" ml-auto">
              <Link className="nav-link NavIteamsStyle" to="/">
                الصفحة الرئيسية
              </Link>
              <Link className="nav-link NavIteamsStyle" to="/Articles">
                مقالات
              </Link>
              <Link
                className="nav-link NavIteamsStyle"
                onClick={this.toggleMenu}
                to="/Researches"
              >
                أبحاث
              </Link>

              <Link className="nav-link NavIteamsStyle" to="/Videos">
                فيديوهات
              </Link>
              <Link className="nav-link NavIteamsStyle" to="/Books">
                كتب
              </Link>

              <Link className="nav-link NavIteamsStyle" to="/links">
                روابط
              </Link>
              <Link className="nav-link NavIteamsStyle" to="/Questions">
                أسئلة
              </Link>
            </Nav>
            <Form inline className="mr-auto navform">
              <FormControl
                type="text"
                placeholder="بحث"
                className="mr-sm-2 NavIteamsStyle"
              />
              <Button
                variant="outline-success"
                className="NavIteamsStyle Navbutton "
              >
                ابحث
              </Button>
            </Form>
          </Navbar.Collapse>
        </Navbar>

        <Route
          path="/"
          exact
          render={() => (
            <Home
              title={this.state.Home.title}
              subTitle={this.state.Home.subTitle}
              text={this.state.Home.text}
            />
          )}
        />
        <Route
          path="/Researches"
          exact
          render={() => (
            <Researches
              title={this.state.Researches.title}
              subTitle={this.state.Researches.subTitle}
              text={this.state.Researches.text}
            />
          )}
        />
        <Route
          path="/Articles"
          exact
          render={() => (
            <Articles
              title={this.state.Articles.title}
              subTitle={this.state.Articles.subTitle}
              text={this.state.Articles.text}
            />
          )}
        />
        <Route
          path="/Videos"
          exact
          render={() => (
            <Videos
              title={this.state.Videos.title}
              subTitle={this.state.Videos.subTitle}
              text={this.state.Videos.text}
            />
          )}
        />
        <Route
          path="/Books"
          exact
          render={() => (
            <Books
              title={this.state.Books.title}
              subTitle={this.state.Books.subTitle}
              text={this.state.Books.text}
            />
          )}
        />
        <Route
          path="/Links"
          exact
          render={() => (
            <Links
              title={this.state.Links.title}
              subTitle={this.state.Links.subTitle}
              text={this.state.Links.text}
            />
          )}
        />
        <Route
          path="/Questions"
          exact
          render={() => (
            <Questions
              title={this.state.Questions.title}
              subTitle={this.state.Questions.subTitle}
              text={this.state.Questions.text}
            />
          )}
        />
        <Route
          path="/Article"
          exact
          render={() => (
            <Article
              title={this.state.Article.title}
              subTitle={this.state.Article.subTitle}
              text={this.state.Article.text}
            />
          )}
        />
        <Route
          path="/Research"
          exact
          render={() => (
            <Research
              title={this.state.Research.title}
              subTitle={this.state.Research.subTitle}
              text={this.state.Research.text}
            />
          )}
        />

        <Route
          path="/Dashboard"
          exact
          render={() => (
            <Dashboard
              title={this.state.Dashboard.title}
              subTitle={this.state.Dashboard.subTitle}
              text={this.state.Dashboard.text}
            />
          )}
        />

        <Route
          path="/Register"
          exact
          render={() => (
            <Register
              title={this.state.Register.title}
              subTitle={this.state.Register.subTitle}
              text={this.state.Register.text}
            />
          )}
        />

        <Route
          path="/Login"
          exact
          render={() => (
            <Login
              title={this.state.Login.title}
              subTitle={this.state.Login.subTitle}
              text={this.state.Login.text}
            />
          )}
        />
      </Router>
    );
  }
}
export default NavbarF;
