import React, { Fragment, useEffect, useState } from "react";
import Maincontent from "../components/Maincontent";
import Container from "react-bootstrap/Container";
import axios from "axios";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import "../components/main.css";
import Spinner from "../components/layout/Spinner";

const Articles = () => {
  const DATE_OPTIONS = {
    weekday: "short",
    year: "numeric",
    month: "short",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
    second: "numeric",
  };
  const currentdate = new Date().toLocaleDateString("en-US", DATE_OPTIONS);

  const [posts, setposts] = useState([]);
  const [loading, setLoading] = useState([false]);
  const [CurrentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(9);

  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      const res = await axios.get("http://localhost:5000/api/articles");
      setposts(res.data);
      setLoading(false);
    };
    fetchPosts();
  }, []);

  // get Current Posts
  const indexOfLastVideo = CurrentPage * postsPerPage;
  const indexOfFirstVideo = indexOfLastVideo - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstVideo, indexOfLastVideo);

  //change Page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (loading)
    return (
      <div>
        <Spinner />
        <h6 className="text-center">...Loading</h6>;
      </div>
    );

  return (
    <div>
      <Maincontent title={props.title} subTitle={props.subTitle} />
      <div className="articlemagestyle"></div>
      <Container fluid>
        <Row className="justify-content-center ">
          <Col xl={4} lg={4} md={6} sm={12} xs={12} className="text-center ">
            <div
              style={{
                height: "350px",
                width: "300px",
                borderStyle: "dotted",
                borderColor: "green",
                backgroundImage: `url("../assets/images/article1.jpg")`,
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
                marginLeft: "auto",
                marginRight: "auto",
              }}>
              {" "}
            </div>
            <h4 className="articletitle">عنوان المقالة </h4>
            <p className=" articletextstyle displayinline">
              ناشر المقالة | {currentdate}
            </p>
            | <p className=" displayinline  articleResource"> مصدر المقالة </p>
            <p className="articlemaxwidth">
              لمحة مختصرة عن المقالة , النقاط الرئيسية في هذه المقالة ,...
            </p>
            <div className="downarticle">
              <div className="articlerectangle"></div>
              <a href="/Article" rel="noopener noreferrer">
                <Button
                  variant="outline-success"
                  size="sm"
                  className="buttonposition">
                  اقرأ المزيد
                </Button>
              </a>

              <div className="articlerectangle2"></div>
            </div>
          </Col>

          <Col xl={4} lg={4} md={6} sm={12} xs={12} className="text-center ">
            <div className="articleimage2style "> </div>
            <h4 className="articletitle">عنوان المقالة </h4>
            <p className=" articletextstyle displayinline">
              ناشر المقالة | {currentdate}
            </p>
            | <p className=" displayinline  articleResource"> مصدر المقالة </p>
            <p className="articlemaxwidth">
              لمحة مختصرة عن المقالة , النقاط الرئيسية في هذه المقالة ,...
            </p>
            <div className="downarticle">
              <div className="articlerectangle"></div>
              <a href="/Article" rel="noopener noreferrer">
                <Button
                  variant="outline-success"
                  size="sm"
                  className="buttonposition">
                  اقرأ المزيد
                </Button>
              </a>

              <div className="articlerectangle2"></div>
            </div>
          </Col>

          <Col xl={4} lg={4} md={6} sm={12} xs={12} className="text-center ">
            <div className="articleimage1style "> </div>
            <h4 className="articletitle">عنوان المقالة </h4>
            <p className=" articletextstyle displayinline">
              ناشر المقالة | {currentdate}
            </p>
            | <p className=" displayinline  articleResource"> مصدر المقالة </p>
            <p className="articlemaxwidth">
              لمحة مختصرة عن المقالة , النقاط الرئيسية في هذه المقالة ,...
            </p>
            <div className="downarticle">
              <div className="articlerectangle"></div>
              <a href="/Article" rel="noopener noreferrer">
                <Button
                  variant="outline-success"
                  size="sm"
                  className="buttonposition">
                  اقرأ المزيد
                </Button>
              </a>

              <div className="articlerectangle2"></div>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Articles;
