/** @format */
import React, { Fragment, useEffect, useState } from "react";
import Table from "react-bootstrap/Table";
import Spinner from "../components/layout/Spinner"
import Pagination from "../DashboardComponents/Pagenation"
import axios from "axios"; import "../components/main.css";
const VideosList = () => {

    const [posts, setposts] = useState([]);
    const [loading, setLoading] = useState([false]);
    const [CurrentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(10);
    useEffect(() => {

        const fetchPosts = async () => {
            setLoading(true);
            const res = await axios
                .get("http://localhost:5000/api/videos")
            setposts(res.data);
            setLoading(false);
        }
        fetchPosts();
    }, []);
    // get Current Posts
    const indexOfLastVideo = CurrentPage * postsPerPage;
    const indexOfFirstVideo = indexOfLastVideo - postsPerPage;
    const currentPosts = posts.slice(indexOfFirstVideo, indexOfLastVideo);

    //change Page 
    const paginate = pageNumber => setCurrentPage(pageNumber);


    if (loading)
        return (
            <div>
                <Spinner />
                <h6 className="text-center">...Loading</h6>;
            </div>
        )
    return (
        <div>
            <Table className="table table-striped">
                <thead>
                    <tr>
                        <th className="text-left" style={{ backgroundColor: "#67726A" }}>Video Title</th>
                    </tr>
                </thead>
                {currentPosts.map(post => (
                    <tbody>
                        <tr>
                            <td style={{ textAlign: "left" }}>
                                <text className="videotitlestyle" style={{ textAlign: "text-left" }}>{post.VideoTitle}</text>
                                <text style={{ marginLeft: "1000px" }} className="videotitlestyle">Delete</text>
                                <text style={{ marginLeft: "50px" }} className="videotitlestyle">Update</text>
                            </td>
                        </tr>
                    </tbody>
                ))}
            </Table>
            <div>
                <Pagination
                    postsPerPage={postsPerPage}
                    totalPosts={posts.length}
                    paginate={paginate}
                />
            </div>
        </div>
    );
}
export default VideosList
