import store from "./store";


const fetchposts = () => {
    const [posts,setposts]=useState([]);
    const [loading,setLoading]=useState([false]);
    const [CurrentPage,SetCurrentPage]=useState(1);
    const [postsPerPage]=useState(9);
  
    useEffect(() => {
      store.dispatch(laodUser());
  
  const fetchPosts=async()=>{
  setLoading(true);
  const res=await axios
  .get("http://localhost:5000/api/videos")
  setposts(res.data);
  setLoading(false);
      }
  }, []);
  fetchPosts();

//get Current Posts
const indexOfLastVideo=CurrentPage*postsPerPage;
const indexOfFirstVideo=indexOfLastVideo-postsPerPage;
const currentPosts=posts.slice(indexOfFirstVideo,indexOfLastVideo);

//change Page 

const paginate=(pageNumber)=>{
  SetCurrentPage(pageNumber)
}
return (
<Videos posts={currentPosts} loading={loading} />

);
<Pagenation postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate}/>

}
export default fetchposts;
