/** @format */
import axios from "axios";
import { setAlert } from "./alert";
import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  AUTH_ERROR,
  USER_LOADED,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
} from "./types";

import setAuthToken from "../utills/setAuthToken";

//Load admin
export const laodUser = () => async (dispatch) => {
  if (localStorage.token) {
    setAuthToken(localStorage.token);
  }

  try {
    const res = await axios.get("http://localhost:5000/api/auth");
    dispatch({
      type: USER_LOADED,
      payload: res.data,
    });
    dispatch(laodUser());
  } catch (err) {
    dispatch({
      type: AUTH_ERROR,
    });
  }
};

// Admin Register

export const register = (FullName, Username, Password) => async (dispatch) => {
  try {
    const res = await axios.post("http://localhost:5000/api/Admins", {
      FullName,
      Username,
      Password,
    });

    dispatch({
      type: REGISTER_SUCCESS,
      payload: res.data,
    });

    dispatch(laodUser());
  } catch (err) {
    const errors = err.response.data.errors;

    if (errors) {
      errors.forEach((error) => dispatch(setAlert(error.msg, "danger")));
    }
    dispatch({
      type: REGISTER_FAIL,
    });
  }
};

//Admin login

export const login = (Username, Password) => async (dispatch) => {
  try {
    const res = await axios.post("http://localhost:5000/api/auth", {
      Username,
      Password,
    });

    dispatch({
      type: LOGIN_SUCCESS,
      payload: res.data,
    });

    dispatch(laodUser());
  } catch (err) {
    const errors = err.response.data.errors;

    if (errors) {
      errors.forEach((error) => dispatch(setAlert(error.msg, "danger")));
    }
    dispatch({
      type: LOGIN_FAIL,
    });
  }
};

//Logout
export const logout = () => (dispatch) => {
  dispatch({ type: LOGOUT });
};
