/** @format */

import axios from "axios";
import { setAlert } from "./alert";
import { GET_VIDEO, VIDEO_ERROR } from "./types";

// Get current video
export const getCurrentVideo = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/videos");

    dispatch({
      type: GET_VIDEO,
      payload: res.data,
    });
  } catch (err) {
    dispatch({
      type: VIDEO_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

//create or update a Video
export const addVideo = (formData, history, edit = false) => async (
  dispatch
) => {
  try {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const res = await axios.post(
      "http://localhost:5000/api/videos",
      formData,
      config
    );

    dispatch({
      type: GET_VIDEO,
      payload: res.data,
    });

    dispatch(setAlert(edit ? "Video Updated" : "Video Added"));

    if (!edit) {
      history.push("/dashboard");
    }
  } catch (err) {
    const errors = err.response.data.errors;

    if (errors) {
      errors.forEach((error) => dispatch(setAlert(error.msg, "danger")));
    }
    dispatch({
      type: VIDEO_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};
