/** @format */
import React, { Fragment, useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import NavbarF from "./components/layout/NavbarF";
import Footer from "./components/layout/Footer";
import register from "./DashboardComponents/auth/register";
import Pagenation from "./DashboardComponents/Pagenation"
import Dashboard from "./DashboardComponents/Dashboard";
import login from "./DashboardComponents/auth/login";
import setAuthToken from "./utills/setAuthToken";
import "../src/components/rtl.css";
import { Provider } from "react-redux";
import store from "./store";
import { laodUser } from "./actions/auth";
import Articles from "./Pages/Articles";
import PrivateRoute from "./components/routing/PrivateRoute";
import Researches from "./Pages/Researches";
import Videos from "./Pages/Videos";
import Books from "./Pages/Books";
import Links from "./Pages/Links";
import Questions from "./Pages/Questions";
import Home from "./Pages/Home";
import AddVideo from "./DashboardComponents/AddVideo";
import VideosList from "./DashboardComponents/VideosList"
import Pagination from "./DashboardComponents/Pagenation"
import axios from "axios";
if (localStorage.token) {
  setAuthToken(localStorage.token);
}

const App = () => {
  /*const [posts,setposts]=useState([]);
  const [loading,setLoading]=useState([false]);
  const [CurrentPage,SetCurrentPage]=useState(1);
  const [postsPerPage]=useState(9);*/

  useEffect(() => {
    store.dispatch(laodUser());

    /*const fetchPosts=async()=>{
    setLoading(true);
    const res=await axios
    .get("http://localhost:5000/api/videos")
    setposts(res.data);
    setLoading(false);
        }
        fetchPosts();*/
  }, []);

  //get Current Posts
  //const indexOfLastVideo=CurrentPage*postsPerPage;
  //const indexOfFirstVideo=indexOfLastVideo-postsPerPage;
  //const currentPosts=posts.slice(indexOfFirstVideo,indexOfLastVideo);

  //change Page 

  /*const paginate=(pageNumber)=>{
    SetCurrentPage(pageNumber)
  }*/


  return (
    <Provider store={store}>
      <Router>
        <Fragment>
          <NavbarF />
          <section className="container-fluid">
            <Switch>
              <PrivateRoute exact path="/register" component={register} />
              <PrivateRoute exact path="/Dashboard" component={Dashboard} />
              <PrivateRoute exact path="/AddVideo" component={AddVideo} />
              <PrivateRoute exact path="/VideosList" component={VideosList} />
              <Route exact path="/login" component={login} />
              <Route exact path="/Articles" component={Articles} />
              <Route exact path="/" component={Home} />
              <Route exact path="/Researches" component={Researches} />
              {/* <Route to="/Videos" render={()=><Videos posts={currentPosts} loading={loading}/> } />
             <Pagination
        postsPerPage={postsPerPage}
        totalPosts={posts.length}
        paginate={paginate}
      />  */}
              <Route exact path="/Home" component={Home} />{" "}
              <Route exact path="/Books" component={Books} />{" "}
              <Route exact path="/Videos" component={Videos} />{" "}
              <Route exact path="/Links" component={Links} />{" "}
              <Route exact path="/Questions" component={Questions} />

            </Switch>

          </section>

          { /* <Pagenation postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate}/>
*/}
          <Footer />
        </Fragment>

      </Router>
    </Provider>
  );
};
export default App;
