/** @format */

import axios from "axios";
import { setAlert } from "./alert";
import { VIDEO_ERROR, VIDEO_ADDED, UPDATE_VIDEO, Delete_ERROR } from "./types";

// ADD VIDEO

export const addVideo = (VideoLink, VideoTitle, VideoSummary, VideoPublishDate) => async (
  dispatch
) => {
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };
  const body = JSON.stringify({ VideoLink, VideoTitle, VideoSummary, VideoPublishDate });
  try {
    const res = await axios.post(
      "http://localhost:5000/api/Videos",
      body,
      config
    );

    dispatch({
      type: VIDEO_ADDED,
      payload: res.data,
    });
    dispatch(setAlert("Video has been added", "success"));
  } catch (err) {
    const errors = err.response.data.errors;

    if (errors) {
      errors.forEach((error) => dispatch(setAlert(error.msg, "danger")));
    }
    dispatch({
      type: VIDEO_ERROR,
    });
  }
};
