/** @format */

import axios from "axios";
import { setAlert } from "./alert";

import { GET_VIDEO, VIDEO_ERROR } from "./types";

// Get current video
export const getCurrentVideo = () => async (dispatch) => {
  dispatch(setvideoLoading());
  axios
    .get("/api/video")
    .then((res) =>
      dispatch({
        type: GET_VIDEO,
        payload: res.data,
      })
    )
    .catch((err) =>
      dispatch({
        type: GET_VIDEO,
        payload: { msg: err.response.statusText, status: err.response.status },
      })
    );
};
