/** @format */

import {
  GET_VIDEO,
  VIDEO_LOADING,
  CLEAR_CURRENT_VIDEO,
} from "../actions/types";

const initialState = {
  video: null,
  videos: null,
  loading: false,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case VIDEO_LOADING:
      return {
        ...state,
        loading: true,
      };
    case GET_VIDEO:
      return {
        ...state,
        profile: action.payload,
        loading: false,
      };
    case CLEAR_CURRENT_VIDEO:
      return {
        ...state,
        profile: null,
      };
    default:
      return state;
  }
}
