/** @format */

/*import { GET_VIDEO, VIDEO_ERROR, VIDEO_ADDED } from "../actions/types";

const initialState = {
  video: null,
  videos: null,
  loading: true,
  error: {},
};

export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case GET_VIDEO:
      return {
        ...state,
        loading: false,
        profile: payload,
      };
    case VIDEO_ERROR:
      return {
        ...state,
        video: action.payload,
        loading: false,
      };

    case VIDEO_ADDED:
      return {
        ...state,
      ...payload,
       isAuthenticated: true,
        loading: false,
      };
    default:
      return state;
  }
}
*/
