import React, { useState } from "react";
import Maincontent from "../components/Maincontent";
import Collapse from "react-bootstrap/Collapse";
import "../components/main.css";
import Row from "react-bootstrap/Row";

function Questions(props) {
  const [open1, setOpen1] = useState(false);
  const [open2, setOpen2] = useState(false);
  const [open3, setOpen3] = useState(false);
  const [open4, setOpen4] = useState(false);

  return (
    <div>
      <div className="Questionsimagestyle"> </div>
      <Row className="justify-content-center">
        <text className="stylemaincontent" style={{ marginTop: "-175px" }}>
          {" "}
          أسئلة
        </text>
      </Row>
      <div
        className="questionrectangle "
        onClick={() => setOpen1(!open1)}
        aria-controls="example-collapse-text"
        aria-expanded={open1}>
        <p className="questiontext "> السؤال الأول : نص السؤال</p>
      </div>
      <Collapse in={open1}>
        <p className="questionanswer">الإجابة</p>
      </Collapse>

      <div
        className="questionrectangle "
        onClick={() => setOpen2(!open2)}
        aria-controls="example-collapse-text"
        aria-expanded={open2}>
        <p className="questiontext "> السؤال الثاني : نص السؤال</p>
      </div>
      <Collapse in={open2}>
        <p className="questionanswer">الإجابة</p>
      </Collapse>

      <div
        className="questionrectangle "
        onClick={() => setOpen3(!open3)}
        aria-controls="example-collapse-text"
        aria-expanded={open3}>
        <p className="questiontext "> السؤال الثالث : نص السؤال</p>
      </div>
      <Collapse in={open3}>
        <p className="questionanswer">الإجابة</p>
      </Collapse>

      <div
        className="questionrectangle "
        onClick={() => setOpen4(!open4)}
        aria-controls="example-collapse-text"
        aria-expanded={open4}>
        <p className="questiontext "> السؤال الرابع : نص السؤال</p>
      </div>
      <Collapse in={open4}>
        <p className="questionanswer">الإجابة</p>
      </Collapse>
    </div>
  );
}

export default Questions;
