import React from "react";
import Card from "../components/Card";
import book1 from "../assets/images/book1.jpg";
import book2 from "../assets/images/book2.jpg";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 0,
          title: "Book 1",
          subTitle: "Description  ",
          imgSrc: book1,
          link: "https://books.google.com/",
          selected: false,
        },
        {
          id: 1,
          title: "Book 2 ",
          subTitle: "Description",
          imgSrc: book2,
          link: "https://books.google.com/",
          selected: false,
        },

        {
          id: 2,
          title: "Book 3 ",
          subTitle: "Description",
          imgSrc: book1,
          link: "https://books.google.com/",
          selected: false,
        },

        {
          id: 3,
          title: "Book 4 ",
          subTitle: "Description",
          imgSrc: book2,
          link: "https://books.google.com/",
          selected: false,
        },

        {
          id: 4,
          title: "Book 5 ",
          subTitle: "Description",
          imgSrc: book1,
          link: "https://books.google.com/",
          selected: false,
        },

        {
          id: 5,
          title: "Book 6 ",
          subTitle: "Description",
          imgSrc: book2,
          link: "https://books.google.com/",
          selected: false,
        },

        {
          id: 6,
          title: "Book 7 ",
          subTitle: "Description",
          imgSrc: book1,
          link: "https://books.google.com/",
          selected: false,
        },

        {
          id: 7,
          title: "Book 8",
          subTitle: "Description",
          imgSrc: book2,
          link: "https://books.google.com/",
          selected: false,
        },

        {
          id: 8,
          title: "Book 9 ",
          subTitle: "Description",
          imgSrc: book1,
          link: "https://books.google.com/",
          selected: false,
        },

        {
          id: 9,
          title: "Book 10 ",
          subTitle: "Description",
          imgSrc: book2,
          link: "https://books.google.com/",
          selected: false,
        },
      ],
    };
  }

  handleCardClick = (id, card) => {
    let items = [...this.state.items];

    items[id].selected = items[id].selected ? false : true;

    items.forEach((item) => {
      if (item.id !== id) {
        item.selected = false;
      }
    });

    this.setState({
      items,
    });
  };

  makeItems = (items) => {
    return items.map((item) => {
      return (
        <Card
          item={item}
          click={(e) => this.handleCardClick(item.id, e)}
          key={item.id}
        />
      );
    });
  };

  render() {
    return (
      <Container fluid={true}>
        <Row className="justify-content-center">
          {this.makeItems(this.state.items)}
        </Row>
      </Container>
    );
  }
}

export default Carousel;
